//ใช้กำหนดว่า คนloginแล้ว เข้าหน้าไหนได้บ้าง
import TodoPage from './component/page/Index';
import LoginPage from './component/page/Login';
import ProfilePage from './component/page/Profile';
import RegisterPage from './component/page/Register'

const components = {
    todo:{
        url:"/todo",
        component: TodoPage
    },
    login:{
        url: "/login",
        component: LoginPage

    },
    profile:{
        url:"/profile",
        component: ProfilePage
    },
    register:{
        url :"/register",
        component: RegisterPage

    }
};
//Role ไหน เข้าหน้าไหนได้บ้าง
export default {
    guest: {
        allowedRoutes:[
            components.login,
            components.register

        ],
        redirectRoutes:"/login"
    },
    user: {
        allowedRoutes:[
            component.todo,
            component.profile,
            component.login

        ],
        redirectRoutes:"/profile"
    }
    }
